import React, { useState } from 'react';
import Login from '../components/login-form';
import Register from '../components/register-form';

function Log() {
  const [isRegistering, setIsRegistering] = useState(false);

  const toggleForm = () => {
    setIsRegistering(!isRegistering);
  };

  return (
    <div className="Log">
      <div className={`log-container ${isRegistering ? 'register-mode' : ''}`}>
        <div className="logform-container">
          <Login />
          <Register />
        </div>
        <div className="logoverlay-container">
          <div className="logoverlay">
            <div className="logoverlay-panel overlay-left">
              <h1>¡Bienvenido de nuevo!</h1>
              <p>Para mantenerse conectado con nosotros, por favor inicie sesión con su información personal</p>
              <button className="ghost" onClick={toggleForm}>Iniciar Sesión</button>
            </div>
            <div className="logoverlay-panel overlay-right">
              <h1>¡Hola, Amigo!</h1>
              <p>Ingrese sus datos personales y comience su viaje con nosotros</p>
              <button className="ghost" onClick={toggleForm}>Registrarse</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Log;
