// AnimatedContainer.js
import React, { useState } from 'react';
import '../styles';

const AnimatedContainer = () => {
  const [isHovered, setIsHovered] = useState(false);
  const [isContentVisible, setIsContentVisible] = useState(false);

  const handleMouseEnter = () => {
    if (!isHovered) {
      setIsHovered(true);
      setIsContentVisible(true); // Para que el contenido se quede visible
    }
  };

  return (
    <div className="container" onMouseEnter={handleMouseEnter}>
      <div className={`airplane ${isHovered ? 'fly' : ''}`}></div>
      <div className={`content ${isContentVisible ? 'show' : ''}`}>
        <h1>Contenido Revelado</h1>
        <p>Este contenido aparece cuando el avi√≥n pasa sobre el contenedor.</p>
      </div>
    </div>
  );
};

export default AnimatedContainer;
